    private void Produto(){
        String nome;
        double quantidade, valor;
        float codbar;
        
        nome=txtNomeProduto.getText();
        quantidade=Double.parseDouble(txtQuantidade.getText());
        valor=Double.parseDouble(txtValor.getText());
        codbar=Float.parseFloat(txtCodBarras.getText());
        
        ProdutoDTO objProdutoDTO=new ProdutoDTO();
        objProdutoDTO.setCodbar(codbar);
        objProdutoDTO.setNome(nome);
        objProdutoDTO.setQuantidade(quantidade);
        objProdutoDTO.setValor(valor);
        
        ProdutoDAO objProdutoDAO=new ProdutoDAO();
        objProdutoDAO.ImprimirProduto(objProdutoDTO);
    }
    
    private void limparCampos(){
        txtCodBarras.setText("");
        txtNomeProduto.setText("");
        txtQuantidade.setText("");
        txtValor.setText("");
        
        txtNomeProduto.requestFocus();
    }
